import React, { useState, useEffect } from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { useHistory } from "react-router-dom";
import { StyledButton, StyledDialog, StyledTable } from "../../components";
import "../../assets/App.css";


import { DataGrid } from '@material-ui/data-grid';

import * as customerActions from "../../reducers/customer/actions";

function mapStateToProps(state) {
  return {
    customer: state.customer
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({ ...customerActions }, dispatch)
  };
}

const customerListPage = (props) => {
  const { customer, actions } = props;
  const [customers, setCustomers] = useState([]);
  const [open, setOpen] = React.useState(false);
  const [id, setId] = React.useState();
  const history = useHistory();

  useEffect(() => {
    actions.getCustomers();
  });

  useEffect(() => {
    setCustomers(customer.form.fields.data);
  }, [customer.form.fields.data]);

  const eventAdd = () => {
    history.push("/form/add");
  };

  const handleDelete = (id) => {
    setId(id);
    setOpen(true);
  };

  const eventDelete = (id) => {
    actions.deleteCustomer(id);
    setOpen(false);
  };

  const handleEdit = (id) => {
    history.push("/form/update/" + id);
  };

  const columns = [
    { field: 'full_name',  headerName: "Full name", width: 70 },
    { field: 'email', headerName: "E-mail", width: 150 },
    { field: 'dob', eaderName: "DOB", width: 150 },
    { field: 'cust_code', headerName: "CustCode" }
  ];



  const rows = [
    { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
    { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
    { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
    { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
    { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
    { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
    { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
    { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
    { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
  ];






  return (
    <div style={{ height: 400, width: "100%" }}>
      <StyledDialog
        open={open}
        message="Are you sure you want to delete this customer?"
        handleOkay={() => {
          eventDelete(id);
        }}
        handleClose={() => {
          setOpen(false);
        }}
      />

      <div align="right" class="space">
        <StyledButton
          label="Add customer"
          onClick={() => {
            eventAdd();
          }}
          color="primary"
        />
      </div>


      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={5}
        rowsPerPageOptions={[5]}
        checkboxSelection
      />


    </div>
  );
};

export default connect(mapStateToProps, mapDispatchToProps)(customerListPage);
